mapper.select:
select
  id, project_id, power_generation_facilities_address, power_generation_facilities_address_lan_lon, power_generation_facilities_address_screenshot, site_photo, site_near_photo, site_door_photo, contract, project_name, power_generation_facility_owner_name, site_consumer_name, grid_operator_name, contract_key_info_en, contract_screenshot,  primary_wiring_diagram, capacity, site_consumptive_electricity_list, site_consumptive_electricity_sales_invoice, grid_connected_electricity_list, grid_connected_electricity_sales_invoice, meter, site_consumptive_electricity_list_screenshot_desc, creator_user_id, creator_user_name, is_delete, created_at, updated_at
from project_registration_data


        and id = #{id}


        and project_id = #{projectId}


        and power_generation_facilities_address = #{powerGenerationFacilitiesAddress}


        and power_generation_facilities_address_lan_lon = #{powerGenerationFacilitiesAddressLanLon}


        and power_generation_facilities_address_screenshot = #{powerGenerationFacilitiesAddressScreenshot}


        and power_generation_facilities_address_screenshot_desc = #{powerGenerationFacilitiesAddressScreenshotDesc}


        and power_generation_facilities_address_screenshot_desc_en = #{powerGenerationFacilitiesAddressScreenshotDescEn}


        and site_photo = #{sitePhoto}


        and site_near_photo = #{siteNearPhoto}


        and site_door_photo = #{siteDoorPhoto}


        and contract = #{contract}


        and project_name = #{projectName}


        and power_generation_facility_owner_name = #{powerGenerationFacilityOwnerName}


        and site_consumer_name = #{siteConsumerName}


        and grid_operator_name = #{gridOperatorName}


        and contract_key_info_en = #{contractKeyInfoEn}


        and contract_screenshot = #{contractScreenshot}


        and contract_screenshot_desc = #{contractScreenshotDesc}


        and contract_screenshot_desc_en = #{contractScreenshotDescEn}


        and primary_wiring_diagram = #{primaryWiringDiagram}


        and capacity = #{capacity}


        and site_consumptive_electricity_list = #{siteConsumptiveElectricityList}


        and site_consumptive_electricity_sales_invoice = #{siteConsumptiveElectricitySalesInvoice}


        and grid_connected_electricity_list = #{gridConnectedElectricityList}


        and grid_connected_electricity_sales_invoice = #{gridConnectedElectricitySalesInvoice}


        and meter = #{meter}


        and site_consumptive_electricity_list_screenshot = #{siteConsumptiveElectricityListScreenshot}


        and site_consumptive_electricity_list_screenshot_desc = #{siteConsumptiveElectricityListScreenshotDesc}


        and site_consumptive_electricity_list_screenshot_desc_en = #{siteConsumptiveElectricityListScreenshotDescEn}


        and site_consumptive_electricity_sales_invoice_screenshot = #{siteConsumptiveElectricitySalesInvoiceScreenshot}


        and site_consumptive_electricity_sales_invoice_screenshot_desc = #{siteConsumptiveElectricitySalesInvoiceScreenshotDesc}


        and site_consumptive_electricity_sales_invoice_screenshot_desc_en = #{siteConsumptiveElectricitySalesInvoiceScreenshotDescEn}


        and grid_connected_electricity_list_screenshot = #{gridConnectedElectricityListScreenshot}


        and grid_connected_electricity_list_screenshot_desc = #{gridConnectedElectricityListScreenshotDesc}


        and grid_connected_electricity_list_screenshot_desc_en = #{gridConnectedElectricityListScreenshotDescEn}


        and grid_connected_electricity_sales_invoice_screenshot = #{gridConnectedElectricitySalesInvoiceScreenshot}


        and grid_connected_electricity_sales_invoice_screenshot_desc = #{gridConnectedElectricitySalesInvoiceScreenshotDesc}


        and grid_connected_electricity_sales_invoice_screenshot_desc_en = #{gridConnectedElectricitySalesInvoiceScreenshotDescEn}


        and creator_user_id = #{creatorUserId}


        and creator_user_name = #{creatorUserName}


        and is_delete = #{isDelete}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from project_registration_data


        and id = #{id}


        and project_id = #{projectId}


        and power_generation_facilities_address = #{powerGenerationFacilitiesAddress}


        and power_generation_facilities_address_lan_lon = #{powerGenerationFacilitiesAddressLanLon}


